trigger LeadTrigger on Lead (before insert) {
  for (Lead lead : Trigger.new) {
      Decimal annualRevenue = lead.AnnualRevenue;

      if (annualRevenue == null || annualRevenue < 1000000) {
          lead.Rating = 'Low'; 
      } else if (annualRevenue < 10000000) {
          lead.Rating = 'Medium';
      } else if (annualRevenue >= 10000000) {
          lead.Rating = 'High';
      }
  }
}

trigger OpportunityTrigger on Opportunity (after update) {
    List<Task> tasksToCreate = new List<Task>();
    for(Opportunity opp : Trigger.new) {
        Opportunity oldOpp = Trigger.oldMap.get(opp.Id);
        if(opp.StageName == 'Closed Lost' && oldOpp.StageName != 'Closed Lost') {
            Task followUpTask = new Task(
                WhatId = opp.AccountId,
                Subject = 'Follow-up on lost opportunity',
                ActivityDate = System.today().addDays(7)
            );
            tasksToCreate.add(followUpTask);
        }
    }
    if(!tasksToCreate.isEmpty()) {
        insert tasksToCreate;
    }
}

trigger AccountRevenueTrigger on Account (after update) {
    List<Task> tasksToCreate = new List<Task>();
    
    for(Account acc : Trigger.new) {
        Account oldAccount = Trigger.oldMap.get(acc.Id);
        
        if(acc.AnnualRevenue > 1000000 && (oldAccount.AnnualRevenue <= 1000000 || oldAccount.AnnualRevenue == null)) {
            Task newTask = new Task(
                WhatId = acc.Id, // Associate the task with the account
                OwnerId = acc.OwnerId, // Assign the task to the account owner
                Subject = 'Notify Admin: Annual Revenue Exceeds $1M',
                Description = 'The annual revenue of ' + acc.Name + ' has exceeded $1,000,000. Please notify the admin.',
                Priority = 'High',
                Status = 'Open'
            );
            tasksToCreate.add(newTask);
        }
    }
    
    insert tasksToCreate;
}

