    Opportunity returnOpportunity = new Opportunity(
        AccountId = opp1.AccountId, 
        Amount = opp1.Amount, 
        CloseDate = opp1.CloseDate, 
        Name = opp2.Name, 
        Probability = opp2.Probability, 
        StageName = opp2.StageName
    );
    return returnOpportunity;
}

