public Integer calculateRange(Integer start, Integer endIndex) { 
    if (start == null || endIndex == null || start == endIndex) return 0; 
    Integer min = Math.min(start, endIndex); 
    Integer max = Math.max(start, endIndex); 
    Integer sum = 0; 
