public Map<String, Integer> mergeMaps(Map<String, Integer> map1, Map<String, Integer> map2) { 
 Map<String, Integer> mergedMap = new Map<String, Integer>(); 
 for (String key : map1.keySet()) { 
 mergedMap.put(key, map1.get(key)); 
 } 
 for (String key : map2.keySet()) { 
 Integer existingValue = mergedMap.get(key); 
 mergedMap.put(key, (existingValue != null ? existingValue : 0) + map2.get(key)); 
 } 
 return mergedMap; 
 }

