public class ListUtil { 
    public List<Integer> getLastThreeElements(List<Integer> inputList) { 
        List<Integer> listToReturn = new List<Integer>(); 

        if(inputList.isEmpty()) { 
            listToReturn.add(-1); 
            listToReturn.add(-1); 
            listToReturn.add(-1); } 
        else if(inputList.size() == 1) { 
            listToReturn.add(-1); 
            listToReturn.add(-1); 
            listToReturn.add(inputList.get(0)); 
        } else if(inputList.size() == 2) { 
            listToReturn.add(-1); 
            listToReturn.add(inputList.get(0)); 
            listToReturn.add(inputList.get(1)); 
        } else { 
            listToReturn.add(inputList.get(inputList.size() - 3)); 
            listToReturn.add(inputList.get(inputList.size() - 2)); 
            listToReturn.add(inputList.get(inputList.size() - 1)); 
        } 

        return listToReturn; 
    } 
}

