trigger ZeroAmountOpportunityCase on Opportunity (after update) { 
    List<Case> casesToCreate = new List<Case>();
    for(Opportunity opp : Trigger.new) {
        Opportunity oldOpp = Trigger.oldMap.get(opp.Id);
        if(opp.Amount == 0 && opp.StageName == 'Closed Won' && (oldOpp == null || oldOpp.StageName != 'Closed Won')) {
            Case newCase = new Case(
                Subject = 'Opportunity Closed with $0 Amount',
                Description = 'This case is created because an opportunity with $0 amount was closed.',
                Priority = 'High',
                Status = 'New',
                Origin = 'Automated',
                RelatedOpportunity__c = opp.Id
            );
            casesToCreate.add(newCase);
        }
    }
    if(!casesToCreate.isEmpty()) {
        insert casesToCreate;
    }
}

