public Map<String, Integer> charOccurrences(String str) {
       Map<String, Integer> charMap = new Map<String, Integer>();
       for(Integer i = 0; i < str.length(); i++) {
           String currentChar = str.substring(i, i + 1);
           if(currentChar != ' ') { // Ignore spaces
               if(charMap.containsKey(currentChar)) {
                   charMap.put(currentChar, charMap.get(currentChar) + 1);
               } else {  charMap.put(currentChar, 1);
               }
           }
       }
       return charMap;
   }

