public class SalaryCalculator {
    public Decimal calculateSalary(Integer hoursWorked, Decimal hourlyRate) {
        if (hoursWorked <= 40) {
            return hoursWorked * hourlyRate;
        } else {
            Integer regularHours = 40;
            Integer overtimeHours = hoursWorked - regularHours;
            Decimal overtimeRate = 1.5 * hourlyRate;
            return (regularHours * hourlyRate) + (overtimeHours * overtimeRate);
        }
    }
}

