public class HousePricingCalculator {
    public Decimal calculateHousePrice(Integer houseAge, Integer houseArea, Decimal basePrice) {
        Decimal finalPrice = basePrice;

        // Apply discount based on age
        if (houseAge > 10) {
            finalPrice -= finalPrice * 0.05; // 5% discount
        } else if (houseAge > 5) {
            finalPrice -= finalPrice * 0.03; // 3% discount
        }

        // Apply increase for large area
        if (houseArea > 150) {
            finalPrice += finalPrice * 0.02; // 2% increase
        }

        return finalPrice;
    }
}

