public class GenePointDNAAnalyzer {
public String analyzeGCContent(String DnaSeq) {
Decimal nrG = DnaSeq.countMatches('G');
Decimal nrC = DnaSeq.countMatches('C');
Decimal dnaLen = DnaSeq.length();
Decimal perc = ((nrG + nrC) / dnaLen) ;
return perc > 0.6 ? 'High GC content' : 'Low GC content';
}
}

