public class OilWellEfficiencyCalculator {
    public String calculateWellEfficiency(Integer dailyProduction, Integer operatingHours) {
        if (operatingHours == 0) {
            return 'Invalid data'; // Avoid division by zero
        }
        Decimal hourlyEfficiency = (Decimal)dailyProduction / operatingHours;

        if (hourlyEfficiency > 100) {
            return 'Highly Efficient';
        } else if (hourlyEfficiency >= 50) {
            return 'Moderately Efficient';
        } else {
            return 'Low Efficiency';
        }
    }
}

