public List<Integer> generateFibonacciSeries(Integer num) {
       Integer first = 0;
       Integer second = 1;
       Integer next;
       List<Integer> fibonacci = new List<Integer>{first, second};
           if (num == 0) {
               return new List<Integer>{first};
                   }else{
                       while (second + first <= num) {
                           next = first + second;
                           fibonacci.add(next);
                           first = second;
                           second = next;
                       }

                       return fibonacci;
                   }

   }

