public class InvestmentCalculator {
    // Calculate the future value of an investment based on initial amount, annual interest rate, and number of years
    public Decimal calculateInvestmentValue(Decimal initialAmount, Decimal annualInterestRate, Integer years) {
        Decimal currentAmount = initialAmount;
        for (Integer i = 1; i <= years; i++) {
            currentAmount += currentAmount * (annualInterestRate / 100); // Compound interest calculation
            System.debug('Year ' + i + ': ' + currentAmount); // Print the value for each year
        }
        return currentAmount; // Return the final value after all years
    }
}

