public class FermatExplorer {
    public String checkFermat(Integer a, Integer b, Integer c, Integer n) {
        if (n > 2) {
            Double aPowerN = Math.pow(a, n);
            Double bPowerN = Math.pow(b, n);
            Double cPowerN = Math.pow(c, n);

            if (aPowerN + bPowerN == cPowerN) {
                return 'Holy smokes, Fermat was wrong!';
            } else {
                return 'No, that doesnt work.';
            }
        }

        return 'No, that doesnt work.';
    }
}

