public class UsernameValidator {
    public boolean validateUsername(String username) {
        // Check for null or empty input
        if (String.isBlank(username)) {
            return false;
        }

        // Check if the username ends with "_job"
        if (!username.endsWith('_job')) {
            return false;
        }

        // Check if the username (excluding "_job") has at least 8 characters
        String usernameWithoutSuffix = username.substring(0, username.length() - 4);
        if (usernameWithoutSuffix.length() < 8) {
            return false;
        }

        // All requirements met
        return true;
    }
}

