public class ListUtils {
    public List<Object> removeListFromList(List<Object> sourceList, List<Object> listToRemove) {
        if (sourceList == null || sourceList.isEmpty() || listToRemove == null || listToRemove.isEmpty()) {
            return sourceList;
        }

        List<Object> elementsToRemove = new List<Object>();

        for (Object item : sourceList) {
            if (listToRemove.contains(item)) {
                elementsToRemove.add(item);
            }
        }

        for (Object item : elementsToRemove) {
            Integer index = sourceList.indexOf(item);
            if (index != -1) {
                sourceList.remove(index);
            }
        }

        return sourceList;
    }
}

