public class StringUtils {
    public List<String> parseStateCapitals(String input, String del1, String del2) {
        List<String> stateCapitalList = new List<String>();

        if (String.isBlank(input) || String.isBlank(del1) || String.isBlank(del2)) {
            return stateCapitalList;
        }

        List<String> stateCaptialPairs = input.split('\\' + del2);

        for (String pair : stateCaptialPairs) {
            List<String> stateCapital = pair.split('\\' + del1);
            if (stateCapital.size() == 2) {
                stateCapitalList.add(stateCapital[0].trim() + ' - ' + stateCapital[1].trim());
            }
        }

        return stateCapitalList;
    }
}

