public class SMSModifier {
    public String modifySMS(String message, String choice) {
        if (message == null || message.trim().length() == 0) {
            return '';
        }

        if (choice == 'A') {
            return repeatMessage(message);
        } else if (choice == 'B') {
            return replaceSpaces(message);
        } else if (choice == 'C') {
            return removeDigits(message);
        } else if (choice == 'D') {
            return reverseMessage(message);
        } else {
            return 'Invalid choice. Please select A, B, C, or D.';
        }
    }

    private String repeatMessage(String message) {
        return message + ' ' + message;
    }

    private String replaceSpaces(String message) {
        return message.replace(' ', '_');
    }

    private String removeDigits(String message) {
        return message.replaceAll('\\d', '');
    }

    private String reverseMessage(String message) {
        return message.reverse();
    }
}

