public class SecretCodeValidator {
    public Boolean validateCode(String code){
        Boolean regExCheck = Pattern.matches('Cod[A-Za-z0-9]{3}', code);
        Boolean hasDigit = code.containsAny('0123456789');
        Integer lowerCaseCount = code.replaceAll('[A-Z0-9]','').length();
        Integer upperCaseCount = code.replaceAll('[a-z0-9]','').length();
        Boolean upperCaseMoreThanlowerCase = upperCaseCount > lowerCaseCount;

        return regExCheck && hasDigit && upperCaseMoreThanlowerCase;
    }
}

