public class CustomerReviews {
    public List<String> reviews = new List<String>();

    public Map<String, Integer> calculateWordFrequency() {
        Map<String, Integer> wordFrequencyMap = new Map<String, Integer>();

        if (reviews.isEmpty()) {
            return wordFrequencyMap;
        }

        for (String review : reviews) {
            String word = '';
            review = review.toLowerCase();

            for (Integer i = 0; i < review.length(); i++) {
                String c = review.substring(i, i + 1);
                if (isLetterOrDigit(c)) {
                    word += c;
                } else if (word.length() > 0) {
                    // Add word to map
                    if (wordFrequencyMap.containsKey(word)) {
                        wordFrequencyMap.put(word, wordFrequencyMap.get(word) + 1);
                    } else {
                        wordFrequencyMap.put(word, 1);
                    }
                    word = ''; // reset the word for the next word
                }
            }
            // Add the last word if there is one
            if (word.length() > 0) {
                if (wordFrequencyMap.containsKey(word)) {
                    wordFrequencyMap.put(word, wordFrequencyMap.get(word) + 1);
                } else {
                    wordFrequencyMap.put(word, 1);
                }
            }
        }

        return wordFrequencyMap;
    }

    private Boolean isLetterOrDigit(String c) {
        // Check if the character is a letter or a digit
        if ((c >= 'a' && c <= 'z') || (c >= '0' && c <= '9')) {
            return true;
        }
        return false;
    }
}

