        Decimal total = 0;
        if (expenses != null) {
            for (Decimal expense : expenses) {
                total += expense;
            }
        }
        return total;
    }

    public Boolean withinBudget(Decimal budget) {
        Decimal total = totalExpenses();
        return total <= budget;
    }
}

