        if (!pointsScored.containsKey(playerName)) {
            pointsScored.put(playerName, new List<Integer>());
        }
        pointsScored.get(playerName).add(points);
    }

    public Map<String, Decimal> averagePointsScored() {
        Map<String, Decimal> averagePoints = new Map<String, Decimal>();

        for (String playerName : pointsScored.keySet()) {
            List<Integer> playerPoints = pointsScored.get(playerName);
            Integer totalPoints = 0;

            for (Integer points : playerPoints) {
                totalPoints += points;
            }

            Decimal average = totalPoints / (Decimal)playerPoints.size();
            averagePoints.put(playerName, average);
        }

        return averagePoints;
    }
}

