public class GymMembershipAnalytics {
    public Map<String, Integer> memberTrainingHours = new Map<String, Integer>();

    public void addMemberTrainingHours(String memberName, Integer trainingHoursInMinutes) {
        memberTrainingHours.put(memberName, trainingHoursInMinutes);
    }

    public Decimal averageTrainingDuration() {
        Decimal totalTrainingHours = 0;

        for (Integer trainingHoursInMinutes : memberTrainingHours.values()) {
            totalTrainingHours += trainingHoursInMinutes;
        }

        Integer totalMembers = memberTrainingHours.size();
        Decimal averageDurationInHours = totalMembers > 0 ? totalTrainingHours / (totalMembers * 60) : 0;

        return averageDurationInHours.setScale(2);
    }
}

