public class LottoSimulation2 {
    public Integer playLotto(Integer num1, Integer num2, Integer num3) {
        List<Integer> usernumbers = new List<Integer>{num1, num2, num3};
        Integer min = 1;
        Integer max = 10;
        for(Integer drawcount=1;drawcount<1000;drawcount++){

            Integer draw1  = (Integer)Math.floor(Math.random() * (max - min + 1)) + min;
            Integer draw2  = (Integer)Math.floor(Math.random() * (max - min + 1)) + min;
            Integer draw3  = (Integer)Math.floor(Math.random() * (max - min + 1)) + min;

            List<Integer> drawnNumbers = new List<Integer>{draw1, draw2, draw3};

            if(usernumbers == drawnNumbers){
                return drawcount;
            }
        }

        return 1000;
    }
}

