public Integer findDifference(List<Integer> numbers) {
        if (numbers == null || numbers.isEmpty()) {
            return null;
        }
        Integer min = numbers[0];
        Integer max = numbers[0];
        for (Integer num : numbers) {
            if (num < min) {
                min = num;
            }
            if (num > max) {
                max = num;
            }
        }
        return max - min;
    }

