public Integer findProduct(List<Integer> numbers) {
       if (numbers == null || numbers.size() < 2) {
           return 0;
       }

       Set<Integer> uniqueNumbers = new Set<Integer>(numbers);

       if (uniqueNumbers.size() < 2) {
           return 0;
       }

       List<Integer> sortedUniqueNumbers = new List<Integer>(uniqueNumbers);

       sortedUniqueNumbers.sort();

       Integer largest = sortedUniqueNumbers[sortedUniqueNumbers.size() - 1];
       Integer secondLargest = sortedUniqueNumbers[sortedUniqueNumbers.size() - 2];

       return largest * secondLargest;
   }

