public class MultipleFinder {

    public List<Integer> findMultiplesOfThree(Integer start, Integer endRange) {
        List<Integer> multiples = new List<Integer>();

        Integer adjustedStart = start + (3 - Math.mod(start, 3));
        if (Math.mod(adjustedStart, 3) != 0) {
            adjustedStart += (3 - Math.mod(adjustedStart, 3));
        }

        for (Integer i = adjustedStart; i <= endRange; i += 3) {
            if (Math.mod(i, 3) == 0) {
                multiples.add(i);
            }
        }

        return multiples;
    }
}

