public class DuplicateCharacterCounter {
   public Integer countDuplicates(String input) {
        if (input == null || input.length() == 0) {
            return 0;
        }

        Map<String, Integer> charCount = new Map<String, Integer>();
        Integer maxDuplicates = 0;

        for (Integer i = 0; i < input.length(); i++) {
            String currentChar = input.substring(i, i+1);

            if (charCount.containsKey(currentChar)) {
                Integer count = charCount.get(currentChar) + 1;
                charCount.put(currentChar, count);

                // Update maxDuplicates if necessary
                if (count > maxDuplicates) {
                    maxDuplicates = count;
                }
            } else {
                charCount.put(currentChar, 1);
            }
        }

        return maxDuplicates > 1 ? maxDuplicates : 0;
    }
}

