public class SmartLightSwitch {
    public String lightSwitch(List<String> buttonInputs) {
        Boolean isOn = null; // Initial state is neither on nor off

        for (String input : buttonInputs) {
            if (input == 'On') {
                if (isOn == true) {
                    isOn = null; // Undo the On state
                } else {
                    isOn = true; // Set the light to On
                }
            } else if (input == 'Off') {
                if (isOn == false) {
                    isOn = null; // Undo the Off state
                } else {
                    isOn = false; // Set the light to Off
                }
            }
        }

        if (isOn == null) {
            return 'Nothing';
        } else if (isOn == true) {
            return 'On';
        } else {
            return 'Off';
        }
    }
}

