public Decimal estimateBatteryLife(Decimal initialBatteryLife, Integer numberOfApplications) {
    Decimal remainingBatteryLife = initialBatteryLife;

    for (Integer i = 0; i < numberOfApplications; i++) {
        remainingBatteryLife *= 0.95;
    }

    return remainingBatteryLife.setScale(1, RoundingMode.HALF_UP);
}

