public class CaseResolutionCalculator {
    public Map<String, Decimal> calculateAverageResolutionTime(List<CaseRecord> caseRecords) {
        Map<String, Decimal> averageResolutionTimeMap = new Map<String, Decimal>();
        Map<String, Integer> caseCountMap = new Map<String, Integer>();

        for (CaseRecord record : caseRecords) {
            String agentId = record.agentId;
            Decimal resolutionTime = record.resolutionTime;

            if (averageResolutionTimeMap.containsKey(agentId)) {
                averageResolutionTimeMap.put(agentId, averageResolutionTimeMap.get(agentId) + resolutionTime);
                caseCountMap.put(agentId, caseCountMap.get(agentId) + 1);
            } else {
                averageResolutionTimeMap.put(agentId, resolutionTime);
                caseCountMap.put(agentId, 1);
            }
        }

        for (String agentId : averageResolutionTimeMap.keySet()) {
            Decimal totalResolutionTime = averageResolutionTimeMap.get(agentId);
            Integer caseCount = caseCountMap.get(agentId);
            averageResolutionTimeMap.put(agentId, totalResolutionTime / caseCount);
        }

        return averageResolutionTimeMap;
    }
}

