public class OpportunityConversionCalculator {
    public Map<String, Decimal> calculateConversionRate(List<OpportunityRecord> opportunityRecords) {
        Map<String, Decimal> conversionRateMap = new Map<String, Decimal>();
        Map<String, Integer> opportunityCountMap = new Map<String, Integer>();
        Map<String, Integer> wonOpportunityCountMap = new Map<String, Integer>();

        for (OpportunityRecord record : opportunityRecords) {
            String salesRepId = record.salesRepId;
            Boolean isWon = record.isWon;

            if (opportunityCountMap.containsKey(salesRepId)) {
                opportunityCountMap.put(salesRepId, opportunityCountMap.get(salesRepId) + 1);
            } else {
                opportunityCountMap.put(salesRepId, 1);
            }

            if (isWon) {
                if (wonOpportunityCountMap.containsKey(salesRepId)) {
                    wonOpportunityCountMap.put(salesRepId, wonOpportunityCountMap.get(salesRepId) + 1);
                } else {
                    wonOpportunityCountMap.put(salesRepId, 1);
                }
            }
        }

        for (String salesRepId : opportunityCountMap.keySet()) {
            Integer totalOpportunities = opportunityCountMap.get(salesRepId);
            Integer wonOpportunities = wonOpportunityCountMap.get(salesRepId) != null ? wonOpportunityCountMap.get(salesRepId) : 0;
            Decimal conversionRate = (wonOpportunities * 100.0) / totalOpportunities;
            conversionRateMap.put(salesRepId, conversionRate);
        }

        return conversionRateMap;
    }
}

public class OpportunityRecord {
    public String salesRepId;
    public Boolean isWon;

    public OpportunityRecord(String salesRepId, Boolean isWon) {
        this.salesRepId = salesRepId;
        this.isWon = isWon;
    }
}

