public class SatisfactionCalculator {
    public Map<String, Decimal> calculateAverageSatisfaction(List<FeedbackRecord> feedbackRecords) {
        Map<String, Decimal> satisfactionSumMap = new Map<String, Decimal>();
        Map<String, Integer> feedbackCountMap = new Map<String, Integer>();

        for (FeedbackRecord record : feedbackRecords) {
            String productId = record.productId;
            Integer rating = record.rating;

            if (satisfactionSumMap.containsKey(productId)) {
                satisfactionSumMap.put(productId, satisfactionSumMap.get(productId) + rating);
                feedbackCountMap.put(productId, feedbackCountMap.get(productId) + 1);
            } else {
                satisfactionSumMap.put(productId, rating);
                feedbackCountMap.put(productId, 1);
            }
        }

        Map<String, Decimal> averageSatisfactionMap = new Map<String, Decimal>();

        for (String productId : satisfactionSumMap.keySet()) {
            Decimal totalSatisfaction = satisfactionSumMap.get(productId);
            Integer feedbackCount = feedbackCountMap.get(productId);
            Decimal averageSatisfaction = totalSatisfaction / feedbackCount;
            averageSatisfactionMap.put(productId, averageSatisfaction);
        }

        return averageSatisfactionMap;
    }
}

public class FeedbackRecord {
    public String productId;
    public Integer rating;

    public FeedbackRecord(String productId, Integer rating) {
        this.productId = productId;
        this.rating = rating;
    }
}

