public class RevenueCalculator {
    public Map<String, Decimal> calculateAnnualRevenue(List<TransactionRecord> transactionRecords) {
        Map<String, Decimal> annualRevenueMap = new Map<String, Decimal>();

        for (TransactionRecord record : transactionRecords) {
            String accountId = record.accountId;
            Decimal amount = record.amount;
            Date transactionDate = record.transactionDate;

            if (transactionDate.year() == Date.today().year()) {
                Decimal currentRevenue = annualRevenueMap.get(accountId);
                if (currentRevenue == null) {
                    currentRevenue = 0;
                }
                annualRevenueMap.put(accountId, currentRevenue + amount);
            }
        }

        return annualRevenueMap;
    }
}

