public class BlackJackCardCounter {
    public Integer countCards(List<Object> cards) {
        Integer countValue = 0;

        for (Object card : cards) {
            try {
                Integer cardValue = (Integer) card;
                if (cardValue >= 2 && cardValue <= 6) {
                    countValue++;
                } else if (cardValue >= 10) {
                    countValue--;
                }
            } catch (TypeException e) {
                try {
                    String cardValue = ((String) card).toUpperCase();
                    if (cardValue.equals('J') || cardValue.equals('Q') || cardValue.equals('K') || cardValue.equals('A')) {
                        countValue--;
                    }
                } catch (TypeException ex) {
                    // Geersiz kart deeri, hata ileme yaplabilir veya gz ard edilebilir
                }
            }
        }

        return countValue;
    }
}

