public class EscapeRoomTimer {
    public String escapeRoomTimer(List<Integer> intervals) {
        Integer totalSeconds = 0;

        // Sum up all intervals
        for (Integer interval : intervals) {
            totalSeconds += interval;
        }

        // Calculate total minutes and remaining seconds
        Integer minutes = totalSeconds / 60;
        Integer seconds = Math.mod(totalSeconds, 60); // Use Math.mod for calculating the remaining seconds

        // Format the time as MM:SS manually
        String formattedMinutes = (minutes < 10) ? '0' + String.valueOf(minutes) : String.valueOf(minutes);
        String formattedSeconds = (seconds < 10) ? '0' + String.valueOf(seconds) : String.valueOf(seconds);
        String formattedTime = formattedMinutes + ':' + formattedSeconds;

        // Return the formatted time
        return formattedTime;
    }
}

