public class ShoppingListOptimizer {
    public List<String> optimizeShoppingList(Map<String, Integer> itemQuantityMap) {
        List<String> optimizedList = new List<String>();

        if (itemQuantityMap.isEmpty()) {
            return optimizedList;
        }

        List<Item> itemList = new List<Item>();

        for (String item : itemQuantityMap.keySet()) {
            Integer quantity = itemQuantityMap.get(item);
            itemList.add(new Item(item, quantity));
        }

        itemList.sort();

        for (Item item : itemList) {
            String formattedItem = item.name + ' (' + item.quantity + ')';
            optimizedList.add(formattedItem);
        }

        return optimizedList;
    }

    private class Item implements Comparable {
        String name;
        Integer quantity;

        public Item(String name, Integer quantity) {
            this.name = name;
            this.quantity = quantity;
        }

        public Integer compareTo(Object other) {
            Item otherItem = (Item) other;
            return otherItem.quantity - this.quantity;
        }
    }
}

