public class WeatherPattern {
    public String identifyWeatherPattern(List<String> weather) {
        if (weather.size() < 7) {
            return 'Insufficient data for a pattern';
        }

        Boolean sunnyStreak = false;
        Boolean rainyStreak = false;
        Boolean alternating = true;

        for (Integer i = 0; i < weather.size() - 2; i++) {
            if (weather[i] == 'Sunny' && weather[i + 1] == 'Sunny' && weather[i + 2] == 'Sunny') {
                sunnyStreak = true;
                break;
            } else if (weather[i] == 'Rainy' && weather[i + 1] == 'Rainy' && weather[i + 2] == 'Rainy') {
                rainyStreak = true;
                break;
            }
        }

        for (Integer i = 0; i < weather.size() - 1; i++) {
            if (weather[i] == weather[i + 1]) {
                alternating = false;
                break;
            }
        }

        if (sunnyStreak) {
            return 'Sunny Streak';
        } else if (rainyStreak) {
            return 'Rainy Streak';
        } else if (alternating) {
            return 'Alternating Pattern';
        }

        return 'No recognizable pattern';
    }
}

