public class MusicScale {
    public String identifyScaleType(List<Integer> notes) {
        if (notes.size() != 8) {
            return 'Insufficient notes for a scale';
        }

        List<Integer> steps = new List<Integer>();
        for (Integer i = 0; i < notes.size() - 1; i++) {
            steps.add(notes[i + 1] - notes[i]);
        }

        List<Integer> majorPattern = new List<Integer>{2, 2, 1, 2, 2, 2, 1};
        List<Integer> minorPattern = new List<Integer>{2, 1, 2, 2, 1, 2, 2};

        if (steps == majorPattern) {
            return 'Major Scale';
        } else if (steps == minorPattern) {
            return 'Minor Scale';
        }

        return 'Unknown Scale';
    }
}

