public class CondimentAdder {
    public List<String> addCondiments(List<String> dishes, String condiment) {
        List<String> result = new List<String>();

        for (String dish : dishes) {
            if (dish == condiment) {
                result.add('sauce');
                result.add(condiment);
                result.add('syrup');
            } else {
                result.add(dish);
            }
        }

        return result;
