public class SentenceSearcher {
    public String sentenceSearcher(String text, String word) {
        // Split the text into sentences using the period as a delimiter
        List<String> sentences = text.split('\\.');

        // Normalize the word to lower case for case-insensitive comparison
        String normalizedWord = word.toLowerCase();

        // Iterate through each sentence
        for (String sentence : sentences) {
            // Check if the sentence contains the word (case-insensitive)
            if (sentence.toLowerCase().contains(normalizedWord)) {
                // Return the sentence with a period at the end
                return sentence.trim() + '.';
            }
        }

        // Return an empty string if the word is not found in any sentence
        return '';
    }
}

