public class WordPluralizer {
    public Set<String> pluralize(List<String> words) {
        // Map to count the occurrences of each word
        Map<String, Integer> wordCountMap = new Map<String, Integer>();

        // Count occurrences of each word
        for (String word : words) {
            if (wordCountMap.containsKey(word)) {
                wordCountMap.put(word, wordCountMap.get(word) + 1);
            } else {
                wordCountMap.put(word, 1);
            }
        }

        // Set to store the results
        Set<String> resultSet = new Set<String>();

        // Add words to the set with or without 's' based on their count
        for (String word : wordCountMap.keySet()) {
            if (wordCountMap.get(word) > 1) {
                resultSet.add(word + 's');
            } else {
                resultSet.add(word);
            }
        }

        return resultSet;
    }
}

