public class PrimeMatrixGenerator {
    public List<List<Integer>> generatePrimeMatrix(Integer n) {
        List<List<Integer>> matrix = new List<List<Integer>>();
        List<Integer> primes = generatePrimes(n * n);

        Integer index = 0;
        for (Integer i = 0; i < n; i++) {
            List<Integer> row = new List<Integer>();
            for (Integer j = 0; j < n; j++) {
                row.add(primes[index++]);
            }
            matrix.add(row);
        }
        return matrix;
    }

    private List<Integer> generatePrimes(Integer count) {
        List<Integer> primes = new List<Integer>();
        Integer num = 2;
        while (primes.size() < count) {
            if (isPrime(num)) {
                primes.add(num);
            }
            num++;
        }
        return primes;
    }

    private Boolean isPrime(Integer num) {
        if (num < 2) return false;
        for (Integer i = 2; i <= Math.sqrt(num); i++) {
            if (Math.mod(num, i) == 0) return false;
        }
        return true;
    }
}

