        if (inputList == null || inputList.isEmpty()) {
            return new List<Integer>();
        }

        // Initialize a new list to store the modified values
        List<Integer> resultList = new List<Integer>();

        // Iterate over each value in the input list
        for (Integer value : inputList) {
            // Check if the value is greater than 1000
            if (value > 1000) {
                resultList.add(value * 2);
            } else {
                resultList.add(value * 3);
            }
        }

        // Return the result list
        return resultList;
    }

