public String findMiddleCharacters(String word) {
        if (word == null || String.isEmpty(word)) {
            return null;
        }

        Integer length = word.length();
        Integer middleIndex = length / 2;

        if (Math.mod(length, 2) == 0) {
            // Word length is even, return the middle two characters
            return word.substring(middleIndex - 1, middleIndex + 1);
        } else {
            // Word length is odd, return the middle character
            return word.substring(middleIndex, middleIndex + 1);
        }
}

