public Decimal calculateAverageExcludingExtremes(List<Decimal> numbers) {
        // Handle edge cases: if the list is null or has less than 3 elements
        if (numbers == null || numbers.size() < 3) {
            return null;
        }

        // Find the largest and smallest numbers
        Decimal largest = numbers[0];
        Decimal smallest = numbers[0];

        for (Decimal num : numbers) {
            if (num > largest) {
                largest = num;
            }
            if (num < smallest) {
                smallest = num;
            }
        }

        // Calculate the sum of all numbers except the largest and smallest
        Decimal sum = 0;
        Integer count = 0;
        for (Decimal num : numbers) {
            if (num != largest && num != smallest) {
                sum += num;
                count++;
            }
        }

        // Calculate the average of the remaining numbers
        Decimal average = sum / count;

        return average;
    }

