public Integer sumOfDigitSquares(Integer num) {
       if (num == null) {

           return 0; 
       }

       integer sum = 0;
       integer n = Math.abs(num); 
       while (n > 0) {
           integer digit = Math.mod(n, 10);
           sum += digit * digit;
           n /= 10;
       }
       return sum;
   }

