public Integer countLowercaseLetters(String input) {
    // Check if the input string is null or empty
    if (input == null || String.isEmpty(input)) {
        return 0; // Return 0 if the input is null or empty
    }

    // Initialize a counter for lowercase letters
    Integer count = 0;

    // Iterate through each character in the string
    for (Integer i = 0; i < input.length(); i++) {
        // Get the current character
        String character = input.substring(i, i + 1);

        // Check if the character is a lowercase letter
        if (character.charAt(0)>= 97 && character.charAt(0) <= 122) {
            count++; // Increment the counter if the character is a lowercase letter
        }
    }

    return count; // Return the count of lowercase letters
}

