public class MarketBasketAnalyzer {
    public Map<String, Integer> findFrequentItemsets(List<Map<String, Object>> transactions) {
        Map<String, Integer> itemCounts = new Map<String, Integer>();

        for (Map<String, Object> transactionss : transactions) {
            List<String> items = (List<String>) transactionss.get('items');

            for (String item : items) {
                if (itemCounts.containsKey(item)) {
                    itemCounts.put(item, itemCounts.get(item) + 1);
                } else {
                    itemCounts.put(item, 1);
                }
            }
        }

        return itemCounts;
    }
}

