public class RoomScheduler {
    public Map<String, Integer> findAvailableRooms(List<Map<String, Object>> rooms) {
        Map<String, Integer> availableSlots = new Map<String, Integer>();

        for (Map<String, Object> room : rooms) {
            String roomName = (String) room.get('roomName');
            List<Map<String, Object>> bookings = (List<Map<String, Object>>) room.get('bookings');
            Integer slots = 0;

            bookings.sort(new BookingComparator());

            for (Integer i = 0; i < bookings.size() - 1; i++) {
                DateTime endTimeCurrent = (DateTime) bookings.get(i).get('endTime');
                DateTime startTimeNext = (DateTime) bookings.get(i + 1).get('startTime');
                if (endTimeCurrent.getTime() < startTimeNext.getTime()) {
                    slots++;
                }
            }

            availableSlots.put(roomName, slots);
        }

        return availableSlots;
    }
}

public class BookingComparator implements Comparator<Map<String, Object>> {
    public Integer compare(Map<String, Object> a, Map<String, Object> b) {
        DateTime startTimeA = (DateTime) a.get('startTime');
        DateTime startTimeB = (DateTime) b.get('startTime');
        return startTimeA.getTime() < startTimeB.getTime() ? -1 : 1;
    }
}

